mapper.select:
select
id, user_id, goods_id, delivery_addr_id, goods_name, goods_count, goods_price, order_channel, status,
create_date, pay_date
from order_info

    
        and id = #{id}
    
    
        and user_id = #{userId}
    
    
        and goods_id = #{goodsId}
    
    
        and delivery_addr_id = #{deliveryAddrId}
    
    
        and goods_name = #{goodsName}
    
    
        and goods_count = #{goodsCount}
    
    
        and goods_price = #{goodsPrice}
    
    
        and order_channel = #{orderChannel}
    
    
        and status = #{status}
    
    
        and create_date = #{createDate}
    
    
        and pay_date = #{payDate}
    

limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from order_info

    
        and id = #{id}
    
    
        and user_id = #{userId}
    
    
        and goods_id = #{goodsId}
    
    
        and delivery_addr_id = #{deliveryAddrId}
    
    
        and goods_name = #{goodsName}
    
    
        and goods_count = #{goodsCount}
    
    
        and goods_price = #{goodsPrice}
    
    
        and order_channel = #{orderChannel}
    
    
        and status = #{status}
    
    
        and create_date = #{createDate}
    
    
        and pay_date = #{payDate}
